        private static string Events_English = @"C:\ProgramData\GameOfKingdom\Events_English.json";

        private static string GetLanguage()
        {
            List<SettingsModel> settings = new();
            string json = File.ReadAllText(SettingsPath);

            if (!String.IsNullOrEmpty(json))
            {
                settings = JsonSerializer.Deserialize<List<SettingsModel>>(json);
                if (settings[0].Language == 0)
                    return "polish";
                else
                    return "english";
            }
            else
            {
                Console.WriteLine("GetLanguage - error log - Settings file is empty");
                return "error";
            }
        }

        public static List<EventModel> ReturnEvents()
        {
            string language = GetLanguage();
            List<EventModel> events = new();
            string json;
            if (language == "polish")
                json = File.ReadAllText(Events_Polish);
            else
                json = File.ReadAllText(Events_English);

            if (!String.IsNullOrEmpty(json))
                events = JsonSerializer.Deserialize<List<EventModel>>(json);
            else
                Console.WriteLine("Return events - error log - Events file is empty");
            return events;
        }

        public static List<CriticalEventModel> ReturnCriticalEvents()
        {
            string language = GetLanguage();
            List<CriticalEventModel> events = new();
            string json;
            if (language == "polish")
