    },\r\n        {\r\n          \"resource\": \"Army\",\r\n          \"value\": 1\r\n        },\r\n        {\r\n          \"resource\": \"Control\",\r\n          \"value\": 2\r\n        },\r\n        {\r\n          \"resource\": \"People\",\r\n          \"value\": -1\r\n        }\r\n      ]\r\n    },\r\n    \"option_2\": {\r\n      \"text\": \"Zwołanie narady z dworzanami w celu mediacji\",\r\n      \"description\": \"Narady pozwoliły ci upewnić swoją pozycję, ale nie było to darmowe.\",\r\n      \"consequences\": [\r\n        {\r\n          \"resource\": \"Food\",\r\n          \"value\": -1\r\n        },\r\n        {\r\n          \"resource\": \"Money\",\r\n          \"value\": -1\r\n        },\r\n        {\r\n          \"resource\": \"Army\",\r\n          \"value\": 0\r\n        },\r\n        {\r\n          \"resource\": \"Control\",\r\n          \"value\": 1\r\n        },\r\n        {\r\n          \"resource\": \"People\",\r\n          \"value\": 1\r\n        }\r\n      ]\r\n    },\r\n    \"option_3\": {\r\n      \"text\": \"Wzmocnienie aparatu policyjnego dla zwiększenia kontroli\",\r\n      \"description\": \"Inwestycja w policę zapewniła ci spokój i zwiększyła siły, ale obciąza to gospodarkę.\",\r\n      \"consequences\": [\r\n        {\r\n          \"resource\": \"Food\",\r\n          \"value\": -2\r\n        },\r\n        {\r\n          \"resource\": \"Money\",\r\n          \"value\": -3\r\n        },\r\n        {\r\n          \"resource\": \"Army\",\r\n          \"value\": 2\r\n        },\r\n        {\r\n          \"resource\": \"Control\",\r\n          \"value\": 3\r\n        },\r\n        {\r\n          \"resource\": \"People\",\r\n          \"value\": -2\r\n        }\r\n      ]\r\n    }\r\n  },\r\n  {\r\n    \"id\": \"04\",\r\n    \"name\": \"Najeźdźcy\",\r\n    \"description\": \"Królestwo jest zagrożone najeźdźcami, którzy zbliżają się do granic. Jakie środki obronne podejmiesz, aby ochronić ludność?\",\r\n    \"option_1\": {\r\n      \"text\": \"Wzmo